import './App.css';
import {useState} from "react";
import "./components/buisnessCard"
import BuissnessCard from './components/buisnessCard';
import PhoneDropDown from './components/phoneDropDown';
import SocialDropDown from './components/socialDropDown';

function App() {

  const [turn, setTurn] = useState("");
  const [hidePhone, setHidePhone] = useState("hide");
  const [hidePhoneSpace, setHidePhoneSpace] = useState("hide");

  const [hideSocial, setHideSocial] = useState("hide");

  return (
    <div className="App">
      <BuissnessCard 
        turn={turn}
        setTurn={setTurn}
        setHidePhone={setHidePhone}
        setHidePhoneSpace={setHidePhoneSpace}
        setHideSocial={setHidePhone}
        />
      <div>
      <PhoneDropDown  
        turn={turn}
        hidePhone={hidePhone}
        hidePhoneSpace={hidePhoneSpace}
      />

      <SocialDropDown
      turn={turn}
      hideSocial={hideSocial}
      setHideSocial={setHideSocial}/>
      </div>
    </div>
  );
}

export default App;
